import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';
import './UserListing.css';

export default function UserListing() {
    const navigate = useNavigate();
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    navigate('/login');
                    return;
                }

                const response = await api.getUsers(token);
                if (response.users) {
                    setUsers(response.users);
                } else {
                    setError(response.message || 'Failed to fetch users');
                }
            } catch (err) {
                setError('Error fetching users: ' + err.message);
            } finally {
                setLoading(false);
            }
        };
        fetchUsers();
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/login');
    };

    if (loading) return <div className="container"><p>Loading...</p></div>;

    return (
        <div className="container">
            <div className="header">
                <h2>User Listing</h2>
                <button onClick={handleLogout} className="logout-btn">Logout</button>
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="users-grid">
                {users.length === 0 ? (
                    <p>No users found</p>
                ) : (
                    users.map(user => (
                        <div key={user._id} className="user-card">
                            <div className="user-photo">
                                <img
                                    src={`http://localhost:5000${user.photo}`}
                                    alt={user.firstName}
                                    onError={(e) => { e.target.src = 'https://via.placeholder.com/150'; }}
                                />
                            </div>
                            <div className="user-info">
                                <h3>{user.firstName} {user.lastName}</h3>
                                <p><strong>Email:</strong> {user.email}</p>
                                <p><strong>Gender:</strong> {user.gender}</p>
                                <p><strong>DOB:</strong> {new Date(user.dob).toLocaleDateString()}</p>
                                {user.hobbies && user.hobbies.length > 0 && (
                                    <p><strong>Hobbies:</strong> {user.hobbies.join(', ')}</p>
                                )}
                                <p className="joined-date"><strong>Joined:</strong> {new Date(user.createdAt).toLocaleDateString()}</p>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
}
